package Loops;

public class LoopAssignment {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++)
		{
			System.out.println("I am Batman");
		}
		System.out.println("================Next program ============================");
		
		for(int i=1;i<10;i++)
		{
			System.out.println("I am Batman " +i);
		}
	}

}
